import { useState } from "react";
import { Notification, Button } from "@czi-sds/components";
import "./styles.css";

function App() {
  const [dismissed, setDismissed] = useState(false);
  const handleClick = () => {
    setDismissed(prev => !prev);
  }
  return (
    <div className="app">
      {
        !dismissed ?
        <Notification
          buttonText="Close"
          buttonOnClick={(event) => setDismissed(true)}
          dismissed={dismissed}
          intent="notice"
        >
          Notification Title
          <div>
            Lorem ipsum, dolor sit amet consectetur adipisicing elit.
            Amet eveniet sapiente, officiis aut possimus suscipit
            assumenda non?
          </div>
        </Notification> : 
        <Button onClick={handleClick} sdsType="primary" sdsStyle="rounded">Reset Notification</Button>
      }
    </div>
  );
}

export default App;
```

