**/App.tsx**

```tsx
import React from "react";
import {
  CellBasic,
  CellHeader,
  TableHeader,
  TableRow,
  Table,
  CellHeaderDirection,
  Icon,
  CellComponent,
  ButtonIcon,
  Button,
  Tag,
} from "@czi-sds/components";
import {
  CellContext,
  ColumnDef,
  flexRender,
  getCoreRowModel,
  getSortedRowModel,
  HeaderContext,
  Row,
  SortingFn,
  SortingState,
  useReactTable,
} from "@tanstack/react-table";
import { faker } from "@faker-js/faker";
import "./styles.css";

type Person = {
  firstName: string;
  lastName: string;
  age: number;
  visits: number;
  status: "relationship" | "complicated" | "single";
  createdAt: Date;
  subRows?: Person[];
};

const newPerson = (): Person => {
  return {
    firstName: faker.person.firstName(),
    lastName: faker.person.lastName(),
    age: faker.number.int(96),
    visits: faker.number.int(1000),
    createdAt: faker.date.between({
      from: new Date("2001-01-01"),
      to: new Date(),
    }),
    status: faker.helpers.shuffle<Person["status"]>([
      "relationship",
      "complicated",
      "single",
    ])[0]!,
  };
};

function makeData(...lens: number[]) {
  const makeDataLevel = (depth = 0): Person[] => {
    const len = lens[depth]!;
    return [...Array(len)].map((): Person => {
      return {
        ...newPerson(),
        subRows: lens[depth + 1] ? makeDataLevel(depth + 1) : undefined,
      };
    });
  };

  return makeDataLevel();
}

declare module "@tanstack/table-core" {
  interface SortingFns {
    sortByLength: SortingFn<unknown>;
  }
}

function createHeaderCell(
  header: HeaderContext<Person, unknown>,
  name: string
) {
  return (
    <CellHeader
      colSpan={header.header.colSpan}
      direction={
        header.column.getIsSorted()
          ? (header.column.getIsSorted() as CellHeaderDirection)
          : undefined
      }
      active={!!header.column.getIsSorted()}
      onClick={header.column.getToggleSortingHandler()}
      hideSortIcon={header.header.id === "actions" ? true : false}
      horizontalAlign={header.header.id === "actions" ? "right" : "left"}
    >
      {name}
    </CellHeader>
  );
}

function createBodyCell(cell: CellContext<Person, unknown>) {
  return (
    <CellBasic
      primaryText={cell.getValue() as string}
      horizontalAlign={cell.column.id === "progress" ? "center" : "left"}
      verticalAlign="center"
      shouldShowTooltipOnHover={false}
    />
  );
}

function App() {
  const [sorting, setSorting] = React.useState<SortingState>([]);
  const data = React.useState(() => makeData(20))[0];

  const columns = React.useMemo<ColumnDef<Person>[]>(
    () => [
      {
        accessorKey: "firstName",
        id: "firstName",
        cell: (cell) => (
          <CellBasic
            primaryText={cell.getValue() as string}
            icon={<Icon sdsSize="s" sdsIcon="person" sdsType="static" />}
            verticalAlign="center"
            iconVerticalAlign="center"
            shouldShowTooltipOnHover={false}
          />
        ),
        header: (header) => createHeaderCell(header, "First Name"),
      },
      {
        accessorFn: (row) => row.lastName,
        id: "lastName",
        cell: (cell) => createBodyCell(cell),
        header: (header) => (
          <CellHeader
            colSpan={header.header.colSpan}
            direction={
              header.column.getIsSorted()
                ? (header.column.getIsSorted() as CellHeaderDirection)
                : undefined
            }
            active={!!header.column.getIsSorted()}
            onClick={header.column.getToggleSortingHandler()}
            shouldShowTooltipOnHover
            tooltipText="This column uses a custom sorting function."
            tooltipSubtitle="Sorts items based on their length."
          >
            Last Name
          </CellHeader>
        ),
        sortingFn: "sortByLength",
      },
      {
        accessorKey: "age",
        cell: (cell) => createBodyCell(cell),
        header: (header) => createHeaderCell(header, "Age"),
      },
      {
        accessorKey: "status",
        id: "status",
        cell: (cell) => (
          <CellComponent verticalAlign="center">
            <Tag
              label={cell.cell.getValue() as string}
              color={
                cell.cell.getValue() === "single"
                  ? "notice"
                  : cell.cell.getValue() === "relationship"
                  ? "positive"
                  : "negative"
              }
              sdsStyle="rounded"
              sdsType="secondary"
              hover={false}
            />
          </CellComponent>
        ),
        header: (header) => createHeaderCell(header, "Status"),
      },
      {
        accessorKey: "createdAt",
        cell: (cell) => (
          <CellBasic
            primaryText={(cell.getValue() as Date).getFullYear().toString()}
            secondaryText={(cell.getValue() as Date).toLocaleString("en-us", {
              month: "long",
              day: "numeric",
            })}
            tooltipProps={{
              title: (cell.getValue() as Date).toLocaleString("en-us"),
              sdsStyle: "dark",
              leaveDelay: 0,
              leaveTouchDelay: 0,
            }}
            verticalAlign="center"
            horizontalAlign="left"
          />
        ),
        header: (header) => createHeaderCell(header, "Created At"),
      },
      {
        id: "actions",
        accessorKey: "actions",
        cell: () => (
          <CellComponent horizontalAlign="right" verticalAlign="center">
            <ul className="actions-list">
              <li>
                <Button
                  sdsStyle="icon"
                  icon="TreeHorizontal"
                  sdsSize="small"
                  sdsType="secondary"
                />
              </li>
              <li>
                <Button
                  sdsStyle="icon"
                  icon="BarChartVertical3"
                  sdsSize="small"
                  sdsType="secondary"
                />
              </li>
              <li>
                <Button
                  sdsStyle="icon"
                  icon="Download"
                  sdsSize="small"
                  on
                  sdsType="secondary"
                />
              </li>
              <li>
                <Button
                  sdsStyle="icon"
                  icon="DotsHorizontal"
                  sdsSize="small"
                  sdsType="secondary"
                />
              </li>
            </ul>
          </CellComponent>
        ),
        header: (header) => createHeaderCell(header, "Actions"),
        enableSorting: false,
      },
    ],
    []
  );

  const table = useReactTable({
    data,
    columns,
    state: {
      sorting,
    },
    onSortingChange: setSorting,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    debugTable: true,
    sortingFns: {
      sortByLength: (
        rowA: Row<Person>,
        rowB: Row<Person>,
        columnId: string
      ): number => {
        const a: string = rowA.getValue(columnId);
        const b: string = rowB.getValue(columnId);

        if (a.length > b.length) return 1;
        if (a.length < b.length) return -1;
        return 0;
      },
    },
  });
  return (
    <div className="app">
      <Table>
        <TableHeader>
          {table.getFlatHeaders().map((header) => (
            <React.Fragment key={header.id}>
              {flexRender(header.column.columnDef.header, header.getContext())}
            </React.Fragment>
          ))}
        </TableHeader>
        <tbody>
          {table
            .getRowModel()
            .rows.slice(0, 10)
            .map((row) => {
              return (
                <TableRow key={row.id}>
                  {row.getVisibleCells().map((cell) => (
                    <React.Fragment key={cell.id}>
                      {flexRender(
                        cell.column.columnDef.cell,
                        cell.getContext()
                      )}
                    </React.Fragment>
                  ))}
                </TableRow>
              );
            })}
        </tbody>
      </Table>
    </div>
  );
}

export default App;

