**/App.tsx**

```tsx
import React from "react";
import {
  CellBasic,
  CellHeader,
  TableHeader,
  TableRow,
  Table,
  Button,
  Icon,
  getSemanticColors,
} from "@czi-sds/components";
import {
  CellContext,
  ColumnDef,
  flexRender,
  getCoreRowModel,
  getPaginationRowModel,
  RowData,
  useReactTable,
} from "@tanstack/react-table";
import { useTheme } from "@mui/material";
import { faker } from "@faker-js/faker";
import "./styles.css";

type StatusTypes = "relationship" | "complicated" | "single";

type Person = {
  [key: string]: unknown;
  firstName: string;
  lastName: string;
  age: number;
  visits: number;
  progress: number;
  status: StatusTypes;
  subRows?: Person[];
};

const newPerson = (): Person => {
  return {
    firstName: faker.person.firstName(),
    lastName: faker.person.lastName(),
    age: faker.number.int(40),
    visits: faker.number.int(1000),
    progress: faker.number.int(100),
    status: faker.helpers.shuffle<Person["status"]>([
      "relationship",
      "complicated",
      "single",
    ])[0]!,
  };
};

function makeData(...lens: number[]) {
  const makeDataLevel = (depth = 0): Person[] => {
    const len = lens[depth]!;
    return [...Array(len)].map((): Person => {
      return {
        ...newPerson(),
        subRows: lens[depth + 1] ? makeDataLevel(depth + 1) : undefined,
      };
    });
  };

  return makeDataLevel();
}

declare module "@tanstack/react-table" {
  interface TableMeta<TData extends RowData> {
    updateData: (rowIndex: number, columnId: string, value: TData) => void;
  }
}

const CellEditor = (cell: CellContext<Person, unknown>) => {
  const theme = useTheme();
  const semanticColors = getSemanticColors({theme});

  const initialValue = cell.getValue();
  // We need to keep and update the state of the cell normally
  const [value, setValue] = React.useState(initialValue);

  // When the input is blurred, we'll call our table meta's updateData function
  const onBlur = () => {
    cell.table.options.meta?.updateData(cell.row.index, cell.column.id, value as Person);
  };

  // If the initialValue is changed external, sync it up with our state
  React.useEffect(() => {
    setValue(initialValue);
  }, [initialValue]);

  return (
    <input
      className="editable-input"
      value={value as string}
      onChange={(e) => setValue(e.target.value)}
      onBlur={onBlur}
      style={{ color: semanticColors?.base?.textPrimary }}
    />
  );
}

// Give our default column cell renderer editing superpowers!
const defaultColumn: Partial<ColumnDef<Person>> = {
  cell: CellEditor,
};

const defaultColumns: ColumnDef<Person>[] = [
  {
    accessorKey: "firstName",
    header: "First Name",
  },
  {
    accessorKey: "lastName",
    header: "Last Name",
  },
  {
    accessorKey: "age",
    header: "Age",
  },
  {
    accessorKey: "visits",
    header: "Visits",
  },
  {
    accessorKey: "status",
    header: "Status",
  },
  {
    accessorKey: "progress",
    header: "Profile Progress",
  },
];

function App() {
  const [log, setLog] = React.useState("");
  const [data, setData] = React.useState(() => makeData(3));
  const [columns] = React.useState<typeof defaultColumns>(() => [
    ...defaultColumns,
  ]);

  // Create the table and pass your options
  const table = useReactTable({
    data,
    columns,
    defaultColumn,
    getCoreRowModel: getCoreRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    // Provide our updateData function to our table meta
    meta: {
      updateData: (rowIndex, columnId, value) => {
        setData(
          (
            old: Person[] // Specify the type of 'old' as Person[]
          ) =>
            old.map((row, index) => {
              if (index === rowIndex) {
                const oldValue = row[columnId]; // No need for type assertion
                setLog(
                  `Row index: ${rowIndex}\nColumn Id: ${columnId}\nOld value: ${oldValue}\nNew value: ${value}`
                );
                return {
                  ...row,
                  [columnId]: value,
                };
              }
              return row;
            })
        );
      },
    },
  });

  // Manage your own state
  const [state, setState] = React.useState(table.initialState);

  // Change table data
  const changeTableData = () => {
    setData(() => makeData(5));
    setLog("");
  };

  // Override the state managers for the table to your own
  table.setOptions((prev) => ({
    ...prev,
    state,
    onStateChange: setState,
    // These are just table options, so if things
    // need to change based on your state, you can
    // derive them here
  }));

  return (
    <div className="app">
      <Table>
        <TableHeader>
          {table.getHeaderGroups().map((headerGroup) =>
            headerGroup.headers.map((header) => (
              <CellHeader key={header.id} hideSortIcon>
                {flexRender(
                  header.column.columnDef.header,
                  header.getContext()
                )}
              </CellHeader>
            ))
          )}
        </TableHeader>
        <tbody>
          {table.getRowModel().rows.map((row) => (
            <TableRow key={row.id}>
              {row.getVisibleCells().map((cell) => (
                <CellBasic
                  key={cell.id}
                  primaryText={
                    flexRender(
                      cell.column.columnDef.cell,
                      cell.getContext()
                    ) as string
                  }
                  shouldShowTooltipOnHover={false}
                />
              ))}
            </TableRow>
          ))}
        </tbody>
      </Table>

      <div className="actions">
        <Button
          startIcon={<Icon sdsIcon="refresh" sdsSize="s" sdsType="button" />}
          sdsStyle="rounded"
          sdsType="primary"
          onClick={changeTableData}
        >
          Change table data
        </Button>
      </div>

      {log && (
        <div className="log">
          <h4>Cell Value Update Log:</h4>
          <pre>{log}</pre>
        </div>
      )}

      {data && (
        <div className="log">
          <h4>Table data:</h4>
          <pre>{JSON.stringify(data, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

