**/App.tsx**

```tsx
import React from "react";
import {
  CellBasic,
  CellHeader,
  TableHeader,
  TableRow,
  Table,
  CellHeaderDirection,
  CellComponent,
  Tag,
  Pagination,
} from "@czi-sds/components";
import {
  ColumnDef,
  flexRender,
  getCoreRowModel,
  getSortedRowModel,
  getPaginationRowModel,
  SortingState,
  useReactTable,
  PaginationState,
  HeaderContext,
  CellContext,
} from "@tanstack/react-table";
import { faker } from "@faker-js/faker";
import "./styles.css";

type Person = {
  firstName: string;
  lastName: string;
  age: number;
  visits: number;
  progress: number;
  status: "relationship" | "complicated" | "single";
  subRows?: Person[];
};

const newPerson = (): Person => {
  return {
    firstName: faker.person.firstName(),
    lastName: faker.person.lastName(),
    age: faker.number.int(96),
    visits: faker.number.int(1000),
    progress: faker.number.int(100),
    status: faker.helpers.shuffle<Person["status"]>([
      "relationship",
      "complicated",
      "single",
    ])[0]!,
  };
};

function makeData(...lens: number[]) {
  const makeDataLevel = (depth = 0): Person[] => {
    const len = lens[depth]!;
    return [...Array(len)].map((): Person => {
      return {
        ...newPerson(),
        subRows: lens[depth + 1] ? makeDataLevel(depth + 1) : undefined,
      };
    });
  };

  return makeDataLevel();
}

function App() {
  const DATA_COUNT = 99;
  const PAGE_SIZE = 3;
  const [sorting, setSorting] = React.useState<SortingState>([]);
  const data = React.useState(() => makeData(DATA_COUNT))[0];

  const [{ pageIndex, pageSize }, setPagination] =
    React.useState<PaginationState>({
      pageIndex: 0,
      pageSize: PAGE_SIZE,
    });

  function createHeaderCell(
    header: HeaderContext<Person, unknown>,
    name: string
  ) {
    return (
      <CellHeader
        key={header.header.id}
        colSpan={header.header.colSpan}
        direction={
          header.column.getIsSorted()
            ? (header.column.getIsSorted() as CellHeaderDirection)
            : undefined
        }
        active={!!header.column.getIsSorted()}
        onClick={header.column.getToggleSortingHandler()}
        horizontalAlign={header.header.id === "createdAt" ? "right" : "left"}
        shouldShowTooltipOnHover={true}
        tooltipText={name}
        tooltipProps={{
          enterDelay: 2000,
          enterNextDelay: 2000,
        }}
      >
        {name}
      </CellHeader>
    );
  }

  function createBodyCell(cell: CellContext<Person, unknown>) {
    return (
      <CellBasic
        key={cell.cell.id}
        primaryText={cell.getValue() as string}
        horizontalAlign={cell.column.id === "progress" ? "center" : "left"}
        verticalAlign="center"
        shouldShowTooltipOnHover={true}
      />
    );
  }

  const columns = React.useMemo<ColumnDef<Person>[]>(
    () => [
      {
        accessorKey: "firstName",
        cell: (cell) => createBodyCell(cell),
        header: (header) => createHeaderCell(header, "First Name"),
      },
      {
        accessorKey: "lastName",
        cell: (cell) => createBodyCell(cell),
        header: (header) => createHeaderCell(header, "Last Name"),
      },
      {
        accessorKey: "age",
        cell: (cell) => createBodyCell(cell),
        header: (header) => createHeaderCell(header, "Age"),
      },
      {
        accessorKey: "visits",
        cell: (cell) => createBodyCell(cell),
        header: (header) => createHeaderCell(header, "Visits"),
      },
      {
        accessorKey: "status",
        cell: (cell) => (
          <CellComponent key={cell.cell.id} verticalAlign="center">
            <Tag
              label={cell.getValue() as string}
              color={
                cell.cell.getValue() === "single"
                  ? "notice"
                  : cell.cell.getValue() === "relationship"
                  ? "positive"
                  : "negative"
              }
              sdsStyle="rounded"
              sdsType="secondary"
            />
          </CellComponent>
        ),
        header: (header) => createHeaderCell(header, "Status"),
      },
      {
        accessorKey: "progress",
        cell: (cell) => createBodyCell(cell),
        header: (header) => createHeaderCell(header, "Profile Progress"),
      },
    ],
    []
  );

  const pagination = React.useMemo(
    () => ({
      pageIndex,
      pageSize,
    }),
    [pageIndex, pageSize]
  );

  const table = useReactTable({
    data,
    columns,
    state: {
      sorting,
      pagination,
    },
    onPaginationChange: setPagination,
    onSortingChange: setSorting,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    debugTable: true,
  });

  return (
    <div className="app">
      <Table>
        <TableHeader>
          {table.getFlatHeaders().map((header) => (
            <React.Fragment key={header.id}>
              {flexRender(header.column.columnDef.header, header.getContext())}
            </React.Fragment>
          ))}
        </TableHeader>
        <tbody>
          {table.getRowModel().rows.map((row) => {
            return (
              <TableRow key={row.id}>
                {row.getVisibleCells().map((cell) => (
                  <React.Fragment key={cell.id}>
                    {flexRender(cell.column.columnDef.cell, cell.getContext())}
                  </React.Fragment>
                ))}
              </TableRow>
            );
          })}
        </tbody>
      </Table>
      <div id="pagination-wrapper">
        <Pagination
          sdsStyle="round"
          pageSize={table.getState().pagination.pageSize}
          onPageChange={(page: number) => {
            table.setPageIndex(page - 1);
          }}
          onNextPage={() => table.nextPage()}
          onPreviousPage={() => table.previousPage()}
          totalCount={DATA_COUNT}
          siblingCount={1}
          currentPage={table.getState().pagination.pageIndex + 1}
          truncateDropdown
        />
      </div>
    </div>
  );
}

export default App;

