```tsx
import React, { SyntheticEvent, useState, useRef, useEffect } from "react";
import { InputDropdown, DropdownMenu, noop } from "@czi-sds/components";
import "./styles.css";

const options = [
  {
    details: "Details",
    name: "Menu Item 1",
  },
  {
    details: "A very long Details for the second Menu Item",
    name: "Menu Item 2",
  },
  {
    name: "Menu Item 3",
  },
];

function App() {
  const [anchorEl, setAnchorEl] = useState<HTMLElement | null>(null);
  const [open, setOpen] = useState(false);
  const [details, setDetials] = useState<string>();
  const [counter, setCounter] = useState<string>();
  const [inputDropdownValue, setInputDropdownValue] = useState<string>();
  const [value, setValue] = useState<
    DefaultDropdownMenuOption | DefaultDropdownMenuOption[] | null
  >([]);
  const [pendingValue, setPendingValue] = useState<
    DefaultDropdownMenuOption | DefaultDropdownMenuOption[] | null
  >([]);

  return (
    <div className="app">
      <InputDropdown 
        label="Label"
        onClick={handleClick}
        sdsStyle="square"
        multiple
        value={inputDropdownValue}
        counter={counter}
      />
      <DropdownMenu
        open={open}
        anchorEl={anchorEl}
        onClose={noop}
        onChange={handleChange}
        search={false}
        multiple
        disableCloseOnSelect
        options={options}
        value={pendingValue}
        onClickAway={handleClickAway}
      />
    </div>
  );

  function handleClick(event: React.MouseEvent<HTMLElement>) {
    if (open) {
      setOpen(false);

      if (anchorEl) {
        anchorEl.focus();
      }

      setAnchorEl(null);
    } else {
      setAnchorEl(event.currentTarget);
      setOpen(true);
    }
  };

  function handleChange(
    _: React.SyntheticEvent<Element, Event>,
    newValue: DefaultDropdownMenuOption | DefaultDropdownMenuOption[] | null
  ){
    setPendingValue(newValue);
    setCounter((newValue as DefaultDropdownMenuOption[])?.length.toString());
  };

  function handleClickAway(){
    if (open) {
      setOpen(false);
    }
    setValue(pendingValue);
  };
}

export default App;
```

